SELECT movie_title, movie_year, CONCAT(director_first_name, director_last_name) AS director_name, CONCAT(actor_first_name, actor_last_name) AS actor_name, role FROM movies JOIN movies_directors ON movies_directors.movie_id = movies.movie_id JOIN directors ON directors.director_id = movies_directors.director_id JOIN movies_cast ON movies_cast.movie_id = movies.movie_id JOIN actors ON actors.actor_id = movies_cast.actor_id GROUP BY movies.movie_time HAVING movies.movie_time = MIN(movies.movie_time);
